const inp=document.querySelectorAll(".inp-file");inp.forEach(e=>{e.addEventListener("input",n=>{const s=document.createElement("div");s.classList.add("progress"),s.classList.add("load"),s.innerHTML=`<span class="progress-loader"></span><p class="progress-name">${e.files[0].name}</p>`,e.parentElement.parentElement.append(s)})});